package event

import (
	//"context"
	"time"
)

type Event struct {
	Version        string    `json:"version"` // Autogenerated from logrus hook
	Timestamp      time.Time `json:"timestamp"`
	Event          string    `json:"event"`
	Level          string    `json:"level"`
	Message        string    `json:"message"`
	Type           string    `json:"type"`
	UUID           string    `json:"uuid"`
	ServiceVersion string    `json:"sericeVersion"`
	ServiceName    string    `json:"serviceName"`
	//Context        context.Context `json:"context,omitempty"`
}

var (
	serviceVersion string
	serviceName    string
	uuid           string
)

// SetServiceName sets the serviceName package variable which is
// included in event logs.
func SetServiceName(s string) {
	serviceName = s
}

// SetServiceVersion sets the serviceVersion package variable which is
// included in event logs.
func SetServiceVersion(s string) {
	serviceVersion = s
}

// SetUUID sets the uuid package variable which is
// included in event logs.
func SetUUID(s string) {
	uuid = s
}
